mapper.select:
select *	
from (
	select

	from review
	where goods_no = #{no}
) t
where rownum BETWEEN (#{section}-1)*10*100+(#{pageNum}-1)*10+1 AND (#{section}-1)*100+#{pageNum}*10
order by rownum

